select AUTEUR,NOMDOCUMENT,DESCRDOC,DOC.IDLOCALISATION || '-' || LOCALISATION,'IPP:'||DOC.IPP || ' - ' || PAT.ETC_NOM_MAR || ' ' || PAT.ETC_NOM || ' ' || PAT.ETC_PRN || ' DDN:' || PAT.ETC_DDN ||'('||PAT.ETC_SEX ||') IEP:'||DOC.IEP || ' du ' || PAS.PAS_PER_DEB || ' au ' || PAS.PAS_PER_FIN,ALIASDOC,TO_CHAR(DATECREATION,'DD/MM/YYYY'),IDDOCUMENT  from TBLDOCUMENTS DOC, TBLLOCALISATION LOC, PA_PAT@PATIENT PAT, PA_PAS@PATIENT PAS WHERE IDDOCUMENT IN (select IDDOCUMENT FROM TBLBAL_SOLR_REP WHERE GEN='0') AND DOC.IDLOCALISATION=LOC.IDLOCALISATION AND DOC.IPP=PAT.PAT_IPP AND DOC.IEP=PAS.IEP_IDE AND ROWNUM<11;